def inbatt(lname):
    db = ConnectorMysql()
    cur = db.cursor()
    sql = f'UPDATE `batt` SET `stay`=1,`outc`=0 WHERE lname="{lname}"'
    print("Check in success!!")
    cur.execute(sql)
    db.commit()
    db.close()

