def insert_inbatt(lname, uname, com):
    db = ConnectorMysql()
    cur = db.cursor()
    sql = f'INSERT INTO `batt`(`lname`, `uname`, `stay`, `outc`, `com`) VALUES ("{lname}","{uname}",1,0,{com})'
